        course.timeslot_id || 
        course.time_slot_id || 
        '';
        
      // Extract time slot number
      let timeSlotNumber = null;
      const matches = timeSlotId.match(/TS(\d+)/i);
      if (matches && matches[1]) {
        timeSlotNumber = parseInt(matches[1]);
      }
      
      // Extract day information
      let dayOfWeek = 
        course.day_of_week || 
        (course.TimeSlot?.day_of_week) || 
        '';
      
      // ENHANCEMENT: If day_of_week is still empty, try to extract from timeslot_id
      if (!dayOfWeek && timeSlotId) {
        const dayCodeMatch = timeSlotId.match(/TS\d+-([A-Z]+)/i);
        if (dayCodeMatch && dayCodeMatch[1]) {
          const dayCode = dayCodeMatch[1].toUpperCase();
          const dayMap: Record<string, string> = {
            'MON': 'Monday',
            'TUE': 'Tuesday',
            'WED': 'Wednesday',
            'THU': 'Thursday',
            'FRI': 'Friday'
          };
          dayOfWeek = dayMap[dayCode] || dayCode;
        }
      }
      
      // Normalize day name
      const dayMap: Record<string, string> = {
        'MON': 'Monday',
        'TUE': 'Tuesday',
        'WED': 'Wednesday',
        'THU': 'Thursday',
        'FRI': 'Friday'
      };
      
      if (dayMap[dayOfWeek]) {
        dayOfWeek = dayMap[dayOfWeek];
      }
      
      // Extract is_core from different possible structures
      const isCore = 
        course.is_core !== undefined ? 
        course.is_core : 
        (course.Course?.is_core || false);
        
      // Extract department ID  
      const departmentId = 
        course.department_id || 
        (course.Course?.department_id) || 
        '';
        
      // Build normalized course object
      const normalizedCourse = {
        // Preserve all original properties
        ...course,
        // Ensure critical fields have consistent naming
        scheduled_course_id: course.scheduled_course_id || '',
        course_id: courseId,
        course_name: courseName,
        professor_name: professorName,
        timeslot_id: timeSlotId,
        time_slot_id: timeSlotId,
        day_of_week: dayOfWeek,
        time_slot_number: timeSlotNumber,
        is_core: isCore,
        department_id: departmentId
      };
      
      if (index < 2) {
        console.log(`Normalized course ${index}:`, normalizedCourse);
      }
      
      return normalizedCourse;
    });
