// src/components/admin/schedules/ScheduleView.tsx
import React, { useState, useEffect, useCallback } from 'react';
import {
  Box,
  Button,
  Container,
  Typography,
  Card,
  CardContent,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  Grid,
  Paper,
  CircularProgress,
  Alert,
  AlertTitle,
  Tabs,
  Tab,
  Tooltip,
 //IconButton,
  TextField
} from '@mui/material';
import { SelectChangeEvent } from '@mui/material/Select';
import AddIcon from '@mui/icons-material/Add';
import RefreshIcon from '@mui/icons-material/Refresh';
//import FilterListIcon from '@mui/icons-material/FilterList';
import WarningIcon from '@mui/icons-material/Warning';
//import InfoIcon from '@mui/icons-material/Info';
import SearchIcon from '@mui/icons-material/Search';
import GetAppIcon from '@mui/icons-material/GetApp';
//import { useNavigate } from 'react-router-dom';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogActions from '@mui/material/DialogActions';

// Import services
import scheduleService from '../../../services/scheduleService';
import departmentService from '../../../services/departmentService';
import programService from '../../../services/programService';
import semesterService from '../../../services/semesterService';
import GenerateSchedule from './GenerateSchedule';

const ScheduleView: React.FC = () => {
  //const navigate = useNavigate();
  
  // State variables
  const [scheduleData, setSchedule] = useState<any>(null);
  const [scheduledCourses, setScheduledCourses] = useState<any[]>([]);
  const [filteredCourses, setFilteredCourses] = useState<any[]>([]);
  const [conflicts, setConflicts] = useState<any[]>([]);
  const [departments, setDepartments] = useState<any[]>([]);
  const [programs, setPrograms] = useState<any[]>([]);
  const [semesters, setSemesters] = useState<any[]>([]);
  
  const [selectedDepartment, setSelectedDepartment] = useState<string>('');
  const [selectedProgram, setSelectedProgram] = useState<string>('all'); // Default to 'all' programs
  const [selectedSemester, setSelectedSemester] = useState<string>('');
  const [searchTerm, setSearchTerm] = useState<string>('');
  const [viewMode, setViewMode] = useState<string>('week');
  const [showGenerateForm, setShowGenerateForm] = useState<boolean>(false);
  const [currentDay, setCurrentDay] = useState<number>(0);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [showResolvedConflicts, setShowResolvedConflicts] = useState<boolean>(false);

  // Add state for available schedules and selected schedule
  const [availableSchedules, setAvailableSchedules] = useState<any[]>([]);
  const [selectedScheduleId, setSelectedScheduleId] = useState<string>('');
  
  // Days and time slots for schedule display
  const daysOfWeek = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'];
  const timeSlots = [
    { id: 1, name: 'Time Slot 1', start: '09:10', end: '10:05' },
    { id: 2, name: 'Time Slot 2', start: '10:20', end: '11:15' },
    { id: 3, name: 'Time Slot 3', start: '11:30', end: '12:25' },
    { id: 4, name: 'Time Slot 4', start: '12:45', end: '14:05' },
    { id: 5, name: 'Time Slot 5', start: '13:30', end: '14:50' },
    { id: 6, name: 'Time Slot 6', start: '17:30', end: '20:30' },
    { id: 7, name: 'Time Slot 7', start: '18:00', end: '21:00' }
  ];

  // Add this helper function near the top of your component
  const normalizeDayName = (day: string | undefined | null): string => {
    if (!day) return '';
    
    // Handle short codes
    const dayMap: Record<string, string> = {
      'MON': 'Monday',
      'TUE': 'Tuesday',
      'WED': 'Wednesday',
      'THU': 'Thursday',
      'FRI': 'Friday'
    };
    
    // If it's a short code, convert it
    if (dayMap[day]) {
      return dayMap[day];
    }
    
    // If it's already a full name, return it
    const fullNames = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'];
    if (fullNames.includes(day)) {
      return day;
    }
    
    // Try to match partial
    for (const [code, fullName] of Object.entries(dayMap)) {
      if (day.toUpperCase().includes(code) || fullName.toLowerCase().includes(day.toLowerCase())) {
        return fullName;
      }
    }
    
    // Default
    return day;
  };
    
  // Add this function to fetch available schedules when semester changes
  const fetchAvailableSchedules = useCallback(async (semesterId: string) => {
    if (!semesterId) return;
    
    try {
      setLoading(true);
      const data = await scheduleService.getSchedulesBySemester(semesterId);
      console.log(`Found ${data.length} schedules for semester ${semesterId}`);
      setAvailableSchedules(data);
      
      // If we have schedules and none is selected yet, select the first one
      if (data.length > 0 && !selectedScheduleId) {
        setSelectedScheduleId(data[0].schedule_id);
        console.log(`Auto-selecting first schedule: ${data[0].schedule_id}`);
      }
    } catch (err) {
      console.error('Error fetching schedules for semester:', err);
      setError('Failed to load schedules for this semester');
    } finally {
      setLoading(false);
    }
  }, [selectedScheduleId]);

  // Get schedule by ID
  const loadScheduleById = useCallback(async (scheduleId: string) => {
    if (!scheduleId) return;
  
    try {
      setLoading(true);
      setError(null);
      
      console.log(`Loading specific schedule: ${scheduleId}`);
      const scheduleData = await scheduleService.getScheduleById(scheduleId);
      const conflictsData = await scheduleService.getScheduleConflicts(scheduleId);
      
      // Debug the structure to help diagnose issues
      console.log("Raw schedule data structure:", {
        id: scheduleData.schedule_id,
        hasScheduledCourses: !!scheduleData.ScheduledCourses,
        scheduledCoursesCount: scheduleData.ScheduledCourses?.length || 0,
        hasCourses: !!scheduleData.courses,
        coursesCount: scheduleData.courses?.length || 0
      });
      
      // CRITICAL FIX: Log entire first course to debug
      if (scheduleData.courses && scheduleData.courses.length > 0) {
        console.log("First course from API:", scheduleData.courses[0]);
      } else if (scheduleData.ScheduledCourses && scheduleData.ScheduledCourses.length > 0) {
        console.log("First scheduled course from API:", scheduleData.ScheduledCourses[0]);
      } else {
        console.log("No courses found in API response");
      }
      
      // Extract courses - priority order: courses → ScheduledCourses → []
      let sourceCourses = [];
      if (scheduleData.courses && scheduleData.courses.length > 0) {
        console.log(`Using ${scheduleData.courses.length} courses from courses array`);
        sourceCourses = scheduleData.courses;
      } else if (scheduleData.ScheduledCourses && scheduleData.ScheduledCourses.length > 0) {
        console.log(`Using ${scheduleData.ScheduledCourses.length} courses from ScheduledCourses array`);
        sourceCourses = scheduleData.ScheduledCourses;
      } else {
        console.log("No courses found in schedule data");
      }
      
      // Process whatever courses we found
      const processedCourses = normalizeCourseData(sourceCourses);
      
      console.log(`Processed ${processedCourses.length} courses`);
      if (processedCourses.length > 0) {
        console.log("Sample processed course:", processedCourses[0]);
      }
      
      // Create enhanced schedule data with processed courses
      const enhancedScheduleData = {
        ...scheduleData,
        courses: processedCourses
      };
      
      setSchedule(enhancedScheduleData);
      setScheduledCourses(processedCourses);
      setFilteredCourses(processedCourses);
      setConflicts(conflictsData);
    } catch (err) {
      console.error(`Error loading schedule ${scheduleId}:`, err);
      setError('Failed to load the selected schedule');
      setSchedule(null);
      setScheduledCourses([]);
      setFilteredCourses([]);
      setConflicts([]);
    } finally {
      setLoading(false);
    }
  }, []);


  const normalizeCourseData = (courses: any[]): any[] => {
    if (!courses || !Array.isArray(courses)) {
      console.log("No courses to normalize or courses is not an array");
      return [];
    }
    
    console.log(`Normalizing ${courses.length} courses`);
    
    return courses.map((course: any, index: number) => {
      // Log the first few courses to understand structure
      if (index < 2) {
        console.log(`Raw course data ${index}:`, course);
      }
      
      // Extract course ID from different possible structures
      const courseId = 
        course.course_id || 
        (course.Course?.course_id) || 
        '';
      
      // Extract course name from different possible structures  
      const courseName = 
        course.course_name || 
        (course.Course?.course_name) || 
        '';
      
      // Extract professor information
      const professorFirstName = 
        course.professor_first_name || 
        (course.Professor?.first_name) || 
        '';
      const professorLastName = 
        course.professor_last_name || 
        (course.Professor?.last_name) || 
        '';
      const professorName = 
        course.professor_name || 
        `${professorFirstName} ${professorLastName}`.trim() || 
        'TBA';
      
      // Extract timeslot information
      const timeSlotId = 
        course.timeslot_id || 
        course.time_slot_id || 
        '';
        
      // Extract time slot number
      let timeSlotNumber = null;
      const matches = timeSlotId.match(/TS(\d+)/i);
      if (matches && matches[1]) {
        timeSlotNumber = parseInt(matches[1]);
      }
      
      // Extract day information
      let dayOfWeek = 
        course.day_of_week || 
        (course.TimeSlot?.day_of_week) || 
        '';
        
      // Normalize day name
      const dayMap: Record<string, string> = {
        'MON': 'Monday',
        'TUE': 'Tuesday',
        'WED': 'Wednesday',
        'THU': 'Thursday',
        'FRI': 'Friday'
      };
      
      if (dayMap[dayOfWeek]) {
        dayOfWeek = dayMap[dayOfWeek];
      }
      
      // Extract is_core from different possible structures
      const isCore = 
        course.is_core !== undefined ? 
        course.is_core : 
        (course.Course?.is_core || false);
        
      // Extract department ID  
      const departmentId = 
        course.department_id || 
        (course.Course?.department_id) || 
        '';
        
      // Build normalized course object
      const normalizedCourse = {
        // Preserve all original properties
        ...course,
        // Ensure critical fields have consistent naming
        scheduled_course_id: course.scheduled_course_id || '',
        course_id: courseId,
        course_name: courseName,
        professor_name: professorName,
        timeslot_id: timeSlotId,
        time_slot_id: timeSlotId,
        day_of_week: dayOfWeek,
        time_slot_number: timeSlotNumber,
        is_core: isCore,
        department_id: departmentId
      };
      
      if (index < 2) {
        console.log(`Normalized course ${index}:`, normalizedCourse);
      }
      
      return normalizedCourse;
    });
  };

  
  
  const handleRefresh = useCallback(async () => {
    if (!selectedSemester) return;
  
    setLoading(true);
    setError(null);
    
    try {
      console.log(`Refreshing schedule data for semester: ${selectedSemester}`);
      
      // First, refresh the list of available schedules
      console.log(`Fetching available schedules for semester: ${selectedSemester}`);
      await fetchAvailableSchedules(selectedSemester);
      
      // If we have a selected schedule, reload it
      if (selectedScheduleId) {
        console.log(`Reloading selected schedule: ${selectedScheduleId}`);
        await loadScheduleById(selectedScheduleId);
      } else {
        console.log('No schedule selected, will try to select one from available schedules');
        // If we don't have a selected schedule but we've just loaded available schedules,
        // the fetchAvailableSchedules function will auto-select the first one
      }
      
      // Reset filters
      setSelectedDepartment('');
      setSelectedProgram('all');
      
    } catch (err) {
      console.error("Error refreshing data:", err);
      setError("Failed to refresh data. Please ensure schedules exist for this semester.");
    } finally {
      setLoading(false);
    }
      
  }, [selectedSemester, selectedScheduleId, fetchAvailableSchedules, loadScheduleById]); // ADD THESE DEPENDENCIES

  // Add this effect to check URL parameters
  useEffect(() => {
      // Check URL params for direct navigation from schedule generation
    const queryParams = new URLSearchParams(window.location.search);
    const semesterParam = queryParams.get('semester');
    const refreshParam = queryParams.get('refresh');

    if (semesterParam) {
      console.log(`URL has semester param: ${semesterParam}, setting as selected`);
      setSelectedSemester(semesterParam);
      
      // If refresh=true is in the URL, force a refresh
      if (refreshParam === 'true') {
        // Use setTimeout to ensure the semester state is updated first
        setTimeout(() => handleRefresh(), 500);
      }
    }
  }, [handleRefresh]);  // Empty dependency array means this runs once on component mount

  // Initial data loading
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        setError(null);
        
        // Get semesters
        const semestersData = await semesterService.getAllSemesters();
        setSemesters(semestersData);
        
        // Get departments and programs
        const departmentsData = await departmentService.getAllDepartments();
        const programsData = await programService.getAllPrograms();
        
        setDepartments(departmentsData);
        setPrograms(programsData);
        
        // If we have semesters, try to get a schedule
        if (semestersData.length > 0) {
          // Find the current semester or use the first one
          const currentSemester = semestersData.find((s: any) => s.is_current) || semestersData[0];
          setSelectedSemester(currentSemester.semester_id);
          
          try {
            // Try to get the active schedule for this semester
            const scheduleData = await scheduleService.getActiveSchedule(currentSemester.semester_id);
            const conflictsData = await scheduleService.getScheduleConflicts(scheduleData.schedule_id);
            
            setSchedule(scheduleData);
            setScheduledCourses(scheduleData.courses || []);
            setFilteredCourses(scheduleData.courses || []);
            setConflicts(conflictsData);
          } catch (err) {
            // No schedule exists yet, show message to create one
            console.log('No active schedule found:', err);
            setError('No active schedule found for the selected semester. Please generate a new schedule.');
          }
        } else {
          setError('No semesters found. Please create a semester before generating schedules.');
        }
      } catch (err) {
        console.error('Error fetching initial data:', err);
        setError('Failed to load data. Please ensure your backend server is running.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Fetch schedule when semester changes
  /*
  useEffect(() => {
    if (selectedSemester) {
      const fetchSchedule = async () => {
        try {
          setLoading(true);
          
          const scheduleData = await scheduleService.getActiveSchedule(selectedSemester);
          const conflictsData = await scheduleService.getScheduleConflicts(scheduleData.schedule_id);
          
          setSchedule(scheduleData);
          setScheduledCourses(scheduleData.courses || []);
          setFilteredCourses(scheduleData.courses || []);
          setConflicts(conflictsData);
          
          setLoading(false);
        } catch (err) {
          console.error('Error fetching schedule for semester:', err);
          setError('Failed to load schedule for selected semester.');
          setLoading(false);
        }
      };
      
      fetchSchedule();
    }
  }, [selectedSemester]);*/

  // Fetch schedules when semester changes
  useEffect(() => {
    if (selectedSemester) {
      fetchAvailableSchedules(selectedSemester);
    }
  }, [selectedSemester, fetchAvailableSchedules]);
  
  // Load the selected schedule when selectedScheduleId changes
  useEffect(() => {
    if (selectedScheduleId) {
      loadScheduleById(selectedScheduleId);
    }
  }, [selectedScheduleId, loadScheduleById]);
  
  // Apply filters when department, program, or search term changes
  useEffect(() => {
    if (!scheduledCourses.length) return;
    
    let filtered = [...scheduledCourses];
    
    // Filter by department
    if (selectedDepartment) {
      filtered = filtered.filter(course => 
        course.department_id === selectedDepartment
      );
    }
    
    // Filter by program - if "all" is selected, don't filter by program
    if (selectedProgram && selectedProgram !== 'all') {
      filtered = filtered.filter(course => 
        course.program_ids?.includes(selectedProgram)
      );
    }
    
    // Filter by search term
    if (searchTerm) {
      const search = searchTerm.toLowerCase();
      filtered = filtered.filter(course => 
        course.course_name.toLowerCase().includes(search) ||
        course.course_id.toLowerCase().includes(search) ||
        (course.professor_name && course.professor_name.toLowerCase().includes(search))
      );
    }
    
    setFilteredCourses(filtered);
  }, [scheduledCourses, selectedDepartment, selectedProgram, searchTerm]);
  
  const handleDepartmentChange = (event: SelectChangeEvent) => {
    const deptId = event.target.value as string;
    setSelectedDepartment(deptId);
    
    // Reset program selection when department changes, but set to "all" instead of empty
    setSelectedProgram('all');
  };
  
  const handleProgramChange = (event: SelectChangeEvent) => {
    setSelectedProgram(event.target.value as string);
    
    // If user selects "all programs", we need to update the filtered courses
    // to show all courses for the selected department
    if (event.target.value === 'all') {
      let filtered = [...scheduledCourses];
      
      // Keep department filter if selected
      if (selectedDepartment) {
        filtered = filtered.filter(course => 
          course.department_id === selectedDepartment
        );
      }
      
      // Apply search filter if any
      if (searchTerm) {
        const search = searchTerm.toLowerCase();
        filtered = filtered.filter(course => 
          course.course_name.toLowerCase().includes(search) ||
          course.course_id.toLowerCase().includes(search) ||
          (course.professor_name && course.professor_name.toLowerCase().includes(search))
        );
      }
      
      setFilteredCourses(filtered);
    }
  };
  
  const handleSemesterChange = (event: SelectChangeEvent) => {
    setSelectedSemester(event.target.value as string);
  };

  // Add handler for schedule selection
  const handleScheduleChange = (event: SelectChangeEvent) => {
    console.log(`Selected schedule changed to: ${event.target.value}`);
    setSelectedScheduleId(event.target.value as string);
  };
  
  const handleSearchChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(event.target.value);
  };
  
  const handleViewModeChange = (event: React.SyntheticEvent, newValue: string) => {
    setViewMode(newValue);
  };
  
  const handleDayChange = (event: React.SyntheticEvent, newValue: number) => {
    setCurrentDay(newValue);
  };
  
  const handleCreateSchedule = () => {
    setShowGenerateForm(true);
  };

  const handleScheduleGenerated = (semesterId: string) => {
    console.log(`Schedule generated for semester: ${semesterId}, refreshing data`);
    setSelectedSemester(semesterId);
    setShowGenerateForm(false);
    // Reload the schedule data
    setTimeout(() => {
      handleRefresh();
    }, 500);
  };


  const handleExport = () => {
    if (!scheduleData || !filteredCourses.length) {
      setError("No schedule data available to export");
      return;
    }

    try {
      // Create a formatted schedule for export
      const semesterName = semesters.find(s => s.semester_id === selectedSemester)?.name || 'Current Semester';
      
      // Create header rows
      let csvContent = "Course Schedule - " + semesterName + "\n";
      csvContent += "Generated on: " + new Date().toLocaleString() + "\n\n";
      
      // Column headers
      csvContent += "Course ID,Course Name,Professor,Day,Time,Room,Type\n";
      
      // Add data rows
      filteredCourses.forEach(course => {
        const timeSlotId = course.time_slot_id;
        const slotNumber = parseInt(timeSlotId.replace(/[^\d]/g, ''));
        const timeSlot = timeSlots.find(ts => ts.id === slotNumber) || { start: "", end: "" };
        
        const row = [
          course.course_id,
          course.course_name,
          course.professor_name || 'TBA',
          course.day_of_week,
          `${timeSlot.start}-${timeSlot.end}`,
          course.room || 'TBA',
          course.is_core ? 'Core' : 'Elective'
        ];
        
        csvContent += row.join(',') + '\n';
      });
      
      // Create and download the file
      const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
      const url = URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.setAttribute('href', url);
      link.setAttribute('download', `schedule_${semesterName.replace(/\s+/g, '_')}.csv`);
      link.style.visibility = 'hidden';
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } catch (err) {
      console.error("Error exporting schedule:", err);
      setError("Failed to export schedule");
    }
  };
  
  // Function to determine background color for course blocks
  const getCourseColor = (course: any) => {
    if (course.is_override) return 'rgba(255, 152, 0, 0.8)'; // Orange for overrides
    if (course.is_core) return 'rgba(25, 118, 210, 0.8)'; // Blue for core courses
    return 'rgba(76, 175, 80, 0.8)'; // Green for electives
  };

  // Helper function to check if a course has conflicts
  const hasConflict = (course: any) => {
    return conflicts.some(conflict => 
      conflict.courses.some((c: any) => c.scheduled_course_id === course.scheduled_course_id) &&
      !conflict.is_resolved
    );
  };

  // Function to find courses for a specific timeslot and day
  const getCoursesForTimeSlot = (slotNumber: number, day: string): any[] => {
    const requestId = Math.random().toString(36).substring(2, 8);
    console.log(`[${requestId}] Looking for courses for slot ${slotNumber} on ${day}`);
    
    // If we don't have schedule data or courses, return empty array
    if (!scheduleData) {
      console.log(`[${requestId}] No schedule data available`);
      return [];
    }
    
    if (!scheduleData.courses || !Array.isArray(scheduleData.courses) || scheduleData.courses.length === 0) {
      console.log(`[${requestId}] No courses in schedule data`, scheduleData);
      return [];
    }
  
    // Log the total number of courses available
    console.log(`[${requestId}] Total courses to search through: ${scheduleData.courses.length}`);
    
    // Log first 2 courses to see their structure
    if (scheduleData.courses.length > 0) {
      console.log(`[${requestId}] First course sample:`, {
        id: scheduleData.courses[0].course_id,
        name: scheduleData.courses[0].course_name,
        day: scheduleData.courses[0].day_of_week,
        timeslot: scheduleData.courses[0].timeslot_id,
        slot_number: scheduleData.courses[0].time_slot_number
      });
    }
    
    // Normalize the day name for comparison
    const normalizedDay = normalizeDayName(day);
    console.log(`[${requestId}] Normalized day: ${normalizedDay}`);
    
    // Get all courses to filter
    const allCourses = scheduleData.courses;
    
    // Find courses that match this slot and day
    const matchingCourses = allCourses.filter((course: any) => {
      // Skip nulls and undefined
      if (!course) return false;
      
      // Basic info for debugging
      const courseId = course.course_id || '';
      const courseName = course.course_name || '';
      
      // Day matching logic - be very flexible here
      const courseDayOfWeek = course.day_of_week || '';
      const courseDayNormalized = normalizeDayName(courseDayOfWeek);
      
      // Compare the normalized days
      const exactDayMatch = courseDayNormalized === normalizedDay;
      
      // Partial matching if exact match fails
      const partialDayMatch = !exactDayMatch && (
        courseDayNormalized.includes(normalizedDay) || 
        normalizedDay.includes(courseDayNormalized) ||
        // Match 3-letter abbreviations
        courseDayNormalized.toUpperCase().includes(normalizedDay.substring(0, 3).toUpperCase()) ||
        normalizedDay.toUpperCase().includes(courseDayNormalized.substring(0, 3).toUpperCase())
      );
      
      const dayMatches = exactDayMatch || partialDayMatch;
      
      // Time slot matching - multiple approaches
      let slotMatches = false;
      
      // 1. Direct time_slot_number comparison
      if (course.time_slot_number === slotNumber) {
        slotMatches = true;
      } 
      // 2. Extract from timeslot_id (e.g., "TS1-MON")
      else {
        const timeSlotId = course.timeslot_id || course.time_slot_id || '';
        const matches = timeSlotId.match(/TS(\d+)/i);
        
        if (matches && matches[1]) {
          const courseSlotNumber = parseInt(matches[1]);
          if (courseSlotNumber === slotNumber) {
            slotMatches = true;
          }
        }
      }
      
      // Log any potential matches for debugging
      if (dayMatches && slotMatches) {
        console.log(`[${requestId}] Found match: ${courseName} (${courseId}) on ${courseDayOfWeek} at slot ${course.timeslot_id || course.time_slot_id}`);
      } else if (dayMatches) {
        console.log(`[${requestId}] Day matched but slot didn't match for: ${courseName} (${courseId}) - Course slot: ${course.time_slot_number}, TimeSlot ID: ${course.timeslot_id || course.time_slot_id}, Looking for slot: ${slotNumber}`);
      } else if (slotMatches) {
        console.log(`[${requestId}] Slot matched but day didn't match for: ${courseName} (${courseId}) - Course day: ${courseDayOfWeek}, Normalized: ${courseDayNormalized}, Looking for day: ${normalizedDay}`);
      }
      
      return dayMatches && slotMatches;
    });
    
    console.log(`[${requestId}] Found ${matchingCourses.length} courses for slot ${slotNumber} on ${day}`);
    return matchingCourses;
  };

  // Function to accept a conflict
  const handleAcceptConflict = async (conflictId: string) => {
    try {
      await scheduleService.resolveConflict(conflictId, {
        action: 'ACCEPT',
        notes: 'Conflict accepted by administrator'
      });
      
      // Update local state
      setConflicts(conflicts.map(conflict => 
        conflict.conflict_id === conflictId 
          ? { ...conflict, is_resolved: true, resolution_notes: 'Conflict accepted by administrator' }
          : conflict
      ));
    } catch (err) {
      console.error("Error accepting conflict:", err);
      setError("Failed to accept conflict");
    }
  };

  // Function to override a conflict
  const handleOverrideConflict = async (conflictId: string) => {
    // In a real implementation, this would open a dialog to collect details
    try {
      // Simplified implementation for now
      setConflicts(conflicts.map(conflict => 
        conflict.conflict_id === conflictId 
          ? { ...conflict, is_resolved: true, resolution_notes: 'Conflict overridden by administrator' }
          : conflict
      ));
    } catch (err) {
      console.error("Error overriding conflict:", err);
      setError("Failed to override conflict");
    }
  };

  // Function to render a course block in the timetable
  const renderCourseBlock = (course: any) => {
    const courseHasConflict = hasConflict(course);
    
    return (
      <Box
        key={course.scheduled_course_id}
        sx={{
          backgroundColor: getCourseColor(course),
          color: 'white',
          p: 1,
          borderRadius: 1,
          mb: 1,
          position: 'relative',
          cursor: 'pointer',
          '&:hover': {
            opacity: 0.9,
          }
        }}
      >
        <Typography variant="subtitle2" fontWeight="bold" noWrap>
          {course.course_name}
        </Typography>
        <Typography variant="caption" display="block" noWrap>
          {course.course_id}
        </Typography>
        <Typography variant="caption" display="block" noWrap>
          Prof. {course.professor_name || 'TBA'}
        </Typography>
        
        {courseHasConflict && (
          <Tooltip title="This course has scheduling conflicts">
            <WarningIcon 
              sx={{ 
                position: 'absolute', 
                top: 5, 
                right: 5, 
                color: 'orange',
                fontSize: 20
              }} 
            />
          </Tooltip>
        )}
      </Box>
    );
  };

  // Render weekly view
  const renderWeekView = () => {
    const viewId = Math.random().toString(36).substring(2, 8);
    console.log(`[${viewId}] Rendering week view with:`, {
      scheduledCoursesCount: scheduledCourses.length,
      scheduleId: scheduleData?.schedule_id || 'none',
    });

    if (scheduledCourses.length > 0) {
      // Log a few sample courses to see their structure
      console.log(`[${viewId}] Sample courses (${Math.min(3, scheduledCourses.length)} of ${scheduledCourses.length}):`, 
        scheduledCourses.slice(0, 3).map(c => ({
          id: c.course_id,
          name: c.course_name,
          scheduled_id: c.scheduled_course_id,
          day: c.day_of_week,
          timeslot: c.timeslot_id || c.time_slot_id,
          slot_number: c.time_slot_number
        }))
      );
    } else {
      console.log(`[${viewId}] No scheduled courses available to display`);
    }

    return (
      <Paper sx={{ mt: 2, overflowX: 'auto' }}>
        <Box sx={{ minWidth: 800 }}>
          <Grid container>
            {/* Time slots column */}
            <Grid item xs={1}>
              <Box sx={{ borderRight: 1, borderColor: 'divider', height: '100%', pt: 7 }}>
                {timeSlots.map(slot => (
                  <Box 
                    key={slot.id} 
                    sx={{ 
                      height: 120, 
                      p: 1, 
                      borderBottom: 1, 
                      borderColor: 'divider',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center'
                    }}
                  >
                    <Typography variant="caption">
                      {slot.start} - {slot.end}
                    </Typography>
                  </Box>
                ))}
              </Box>
            </Grid>

            {/* Days columns */}
            {daysOfWeek.map((day, index) => (
              <Grid item xs key={day}>
                <Box sx={{ borderRight: index < daysOfWeek.length - 1 ? 1 : 0, borderColor: 'divider' }}>
                  {/* Day header */}
                  <Box 
                    sx={{ 
                      p: 2, 
                      borderBottom: 1, 
                      borderColor: 'divider',
                      textAlign: 'center',
                      fontWeight: 'bold',
                      bgcolor: 'grey.100'
                    }}
                  >
                    <Typography variant="subtitle1">{day}</Typography>
                  </Box>

                  {/* Time slots */}
                  {timeSlots.map(slot => {
                    const coursesForSlot = getCoursesForTimeSlot(slot.id, day);
                    console.log(`[${viewId}] Slot ${slot.id} on ${day}: found ${coursesForSlot.length} courses`);

                    return (
                      <Box 
                        key={`${day}-${slot.id}`}
                        sx={{ 
                          height: 120, 
                          p: 1, 
                          borderBottom: 1, 
                          borderColor: 'divider',
                          overflow: 'auto'
                        }}
                      >
                        {coursesForSlot.length > 0 ? (
                          coursesForSlot.map(course => renderCourseBlock(course))
                        ) : (
                        // Optional: add an empty indicator
                        <Typography variant="caption" color="text.secondary">No courses</Typography>
                        )}
                      </Box>
                    );
                  })}
                </Box>
              </Grid>
            ))}
          </Grid>
        </Box>
      </Paper>
    );
  };

  // Render day view
  const renderDayView = () => {
    if (!filteredCourses.length) {
      return (
        <Paper sx={{ p: 3, textAlign: 'center' }}>
          <Typography>No schedule data available or no courses match your filters.</Typography>
        </Paper>
      );
    }

    const day = daysOfWeek[currentDay];
    const dayFilteredCourses = filteredCourses.filter(course => course.day_of_week === day);

    return (
      <Paper sx={{ mt: 2 }}>
        <Box sx={{ p: 3 }}>
          <Typography variant="h6" gutterBottom>
            {day}'s Schedule
          </Typography>
          <Grid container spacing={2}>
            {timeSlots.map(slot => {
              const coursesForSlot = dayFilteredCourses.filter(course => 
                course.time_slot_id === `TS${slot.id}-${day.substring(0, 3).toUpperCase()}`
              );

              return (
                <Grid item xs={12} key={slot.id}>
                  <Paper 
                    elevation={1} 
                    sx={{ 
                      p: 2, 
                      borderLeft: 4, 
                      borderColor: coursesForSlot.length > 0 ? 'primary.main' : 'grey.300'
                    }}
                  >
                    <Typography variant="subtitle2" color="textSecondary">
                      {slot.start} - {slot.end}
                    </Typography>
                    <Box sx={{ mt: 1 }}>
                      {coursesForSlot.length > 0 ? (
                        coursesForSlot.map(course => renderCourseBlock(course))
                      ) : (
                        <Typography variant="body2" color="textSecondary">
                          No courses scheduled
                        </Typography>
                      )}
                    </Box>
                  </Paper>
                </Grid>
              );
            })}
          </Grid>
        </Box>
      </Paper>
    );
  };

  // Function to render the conflicts section
  const renderConflicts = () => {
    const displayConflicts = showResolvedConflicts 
      ? conflicts 
      : conflicts.filter(conflict => !conflict.is_resolved);
    
    if (displayConflicts.length === 0) {
      return (
        <Alert severity="success" sx={{ mt: 3 }}>
          No unresolved conflicts in this schedule.
        </Alert>
      );
    }
    
    return (
      <Paper sx={{ mt: 3, p: 2 }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
          <Typography variant="h6">
            Conflicts ({displayConflicts.length})
          </Typography>
          <Button 
            variant="outlined" 
            size="small"
            onClick={() => setShowResolvedConflicts(!showResolvedConflicts)}
          >
            {showResolvedConflicts ? 'Hide Resolved' : 'Show Resolved'}
          </Button>
        </Box>
        
        <Grid container spacing={2}>
          {displayConflicts.map(conflict => (
            <Grid item xs={12} key={conflict.conflict_id}>
              <Paper 
                elevation={1} 
                sx={{ 
                  p: 2, 
                  borderLeft: 4, 
                  borderColor: conflict.is_resolved ? 'success.main' : 'warning.main'
                }}
              >
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                  <Box>
                    <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
                      {conflict.conflict_type}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {conflict.description}
                    </Typography>
                    <Typography variant="body2">
                      Time: {conflict.day_of_week}, {conflict.time_slot?.name || 'Unknown time slot'}
                    </Typography>
                    
                    {conflict.courses && conflict.courses.length > 0 && (
                      <Box sx={{ mt: 1 }}>
                        <Typography variant="body2" sx={{ fontWeight: 'medium' }}>
                          Affected Courses:
                        </Typography>
                        {conflict.courses.map((course: any) => (
                          <Typography key={course.scheduled_course_id} variant="body2" sx={{ ml: 2 }}>
                            • {course.course_name} (Prof. {course.professor_name})
                          </Typography>
                        ))}
                      </Box>
                    )}
                    
                    {conflict.is_resolved && (
                      <Box sx={{ mt: 1, p: 1, bgcolor: 'success.light', borderRadius: 1 }}>
                        <Typography variant="body2" color="success.contrastText">
                          <strong>Resolution:</strong> {conflict.resolution_notes}
                        </Typography>
                      </Box>
                    )}
                  </Box>
                  
                  {!conflict.is_resolved && (
                    <Box>
                      <Button 
                        variant="outlined" 
                        color="primary" 
                        size="small" 
                        onClick={() => handleOverrideConflict(conflict.conflict_id)}
                        sx={{ mr: 1 }}
                      >
                        Override
                      </Button>
                      <Button
                        variant="contained"
                        color="primary"
                        size="small"
                        onClick={() => handleAcceptConflict(conflict.conflict_id)}
                      >
                        Accept
                      </Button>
                    </Box>
                  )}
                </Box>
              </Paper>
            </Grid>
          ))}
        </Grid>
      </Paper>
    );
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Container maxWidth="xl">
      <Box sx={{ mt: 4, mb: 4 }}>
        {/* Header */}
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
          <Typography variant="h4" component="h1">
            Schedule
          </Typography>
          <Box>
            <Button
              variant="outlined"
              color="primary"
              startIcon={<RefreshIcon />}
              onClick={handleRefresh}
              sx={{ mr: 2 }}
            >
              Refresh
            </Button>
            <Button
              variant="outlined"
              color="primary"
              startIcon={<GetAppIcon />}
              onClick={handleExport}
              sx={{ mr: 2 }}
            >
              Export
            </Button>
            <Button
              variant="contained"
              color="primary"
              startIcon={<AddIcon />}
              onClick={handleCreateSchedule}
            >
              Generate New Schedule
            </Button>
          </Box>
        </Box>
        
        {error && (
          <Alert severity="error" sx={{ mb: 3 }}>
            <AlertTitle>Error</AlertTitle>
            {error}
          </Alert>
        )}
        
        {/* Filters */}
        <Card sx={{ mb: 4 }}>
          <CardContent>
            <Grid container spacing={2} alignItems="center">
              <Grid item xs={12} md={2.4}>
                <FormControl fullWidth variant="outlined">
                  <InputLabel id="semester-select-label">Semester</InputLabel>
                  <Select
                    labelId="semester-select-label"
                    value={selectedSemester}
                    onChange={handleSemesterChange}
                    label="Semester"
                  >
                    {semesters.map((semester: any) => (
                      <MenuItem key={semester.semester_id} value={semester.semester_id}>
                        {semester.name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12} md={2.4}>
                <FormControl fullWidth variant="outlined">
                  <InputLabel id="schedule-select-label">Schedule</InputLabel>
                  <Select
                    labelId="schedule-select-label"
                    value={selectedScheduleId}
                    onChange={handleScheduleChange}
                    label="Schedule"
                    disabled={availableSchedules.length === 0}
                  >
                    {availableSchedules.map((schedule) => (
                      <MenuItem key={schedule.schedule_id} value={schedule.schedule_id}>
                        {schedule.name} {schedule.is_final ? '(Final)' : ''}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12} md={2.4}>
                <FormControl fullWidth variant="outlined">
                  <InputLabel id="department-select-label">Department</InputLabel>
                  <Select
                    labelId="department-select-label"
                    value={selectedDepartment}
                    onChange={handleDepartmentChange}
                    label="Department"
                  >
                    <MenuItem value="">
                      <em>All Departments</em>
                    </MenuItem>
                    {departments.map((department) => (
                      <MenuItem key={department.department_id} value={department.department_id}>
                        {department.name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12} md={2.4}>
                <FormControl fullWidth variant="outlined">
                  <InputLabel id="program-select-label">Program</InputLabel>
                  <Select
                    labelId="program-select-label"
                    value={selectedProgram}
                    onChange={handleProgramChange}
                    label="Program"
                    disabled={!selectedDepartment}
                  >
                    <MenuItem value="all">
                      <em>All Programs</em>
                    </MenuItem>
                    {programs
                      .filter(program => !selectedDepartment || program.department_id === selectedDepartment)
                      .map((program) => (
                        <MenuItem key={program.program_id} value={program.program_id}>
                          {program.name}
                        </MenuItem>
                      ))}
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12} md={2.4}>
                <TextField
                  fullWidth
                  label="Search"
                  variant="outlined"
                  value={searchTerm}
                  onChange={handleSearchChange}
                  placeholder="Search courses"
                  InputProps={{
                    endAdornment: <SearchIcon color="action" />
                  }}
                />
              </Grid>
            </Grid>
          </CardContent>
        </Card>
        
        {/* View Mode Tabs */}
        <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 2 }}>
        <Tabs value={viewMode} onChange={handleViewModeChange}>

<Tab label="Week View" value="week" />

<Tab label="Day View" value="day" />

</Tabs>

</Box>



{/* Day Selection Tabs (for Day View) */}

{viewMode === 'day' && (

<Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 2 }}>

<Tabs value={currentDay} onChange={handleDayChange}>

  {daysOfWeek.map((day, index) => (

    <Tab key={day} label={day} value={index} />

  ))}

</Tabs>

</Box>

)}



{/* Schedule Display */}

{viewMode === 'week' ? renderWeekView() : renderDayView()}



{/* Conflicts */}

{renderConflicts()}



{/* Legend */}

<Box sx={{ mt: 3, display: 'flex', alignItems: 'center', flexWrap: 'wrap' }}>

<Box sx={{ display: 'flex', alignItems: 'center', mr: 3, mb: 1 }}>

<Box sx={{ width: 16, height: 16, bgcolor: 'rgba(25, 118, 210, 0.8)', mr: 1, borderRadius: 1 }} />

<Typography variant="caption">Core Course</Typography>

</Box>

<Box sx={{ display: 'flex', alignItems: 'center', mr: 3, mb: 1 }}>

<Box sx={{ width: 16, height: 16, bgcolor: 'rgba(76, 175, 80, 0.8)', mr: 1, borderRadius: 1 }} />

<Typography variant="caption">Elective Course</Typography>

</Box>

<Box sx={{ display: 'flex', alignItems: 'center', mr: 3, mb: 1 }}>

<Box sx={{ width: 16, height: 16, bgcolor: 'rgba(255, 152, 0, 0.8)', mr: 1, borderRadius: 1 }} />

<Typography variant="caption">Overridden Course</Typography>

</Box>

<Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>

<WarningIcon sx={{ color: 'orange', fontSize: 16, mr: 1 }} />

<Typography variant="caption">Scheduling Conflict</Typography>

</Box>

</Box>

</Box>

{showGenerateForm && (
  <Dialog open={showGenerateForm} onClose={() => setShowGenerateForm(false)} maxWidth="md" fullWidth>
    <DialogTitle>Generate New Schedule</DialogTitle>
    <DialogContent>
      <GenerateSchedule 
        onScheduleGenerated={handleScheduleGenerated} 
        onClose={() => setShowGenerateForm(false)}
      />
    </DialogContent>
    <DialogActions>
      <Button onClick={() => setShowGenerateForm(false)}>Cancel</Button>
    </DialogActions>
  </Dialog>
)}

</Container>



);

};

export default ScheduleView;
