// src/components/admin/courses/CourseForm.tsx

import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Button, 
  Typography, 
  Paper, 
  Grid, 
  Snackbar, 
  Alert,
  Checkbox,
  CircularProgress,
  Breadcrumbs,
  Link as MuiLink,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  SelectChangeEvent,
  FormControlLabel,
  Switch
} from '@mui/material';
import SaveIcon from '@mui/icons-material/Save';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import { Link, useNavigate, useParams, useLocation } from 'react-router-dom';
import { TextInput, NumberInput } from '../../../components/common/FormComponents';

// Import services
import departmentService from '../../../services/departmentService';
import programService from '../../../services/programService';
import courseService, { Course } from '../../../services/courseService';

const CourseForm: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { id } = useParams<{ id: string }>();
  const isEditing = Boolean(id);
  
  // Get programId from query param if present (for new courses)
  const queryParams = new URLSearchParams(location.search);
  const initialProgramId = queryParams.get('programId') || '';
  
  const [programs, setPrograms] = useState<any[]>([]);
  const [course, setCourse] = useState<{
    course_id: string;
    program_id: string;
    name: string;
    description: string;
    duration_minutes: number | '';
    is_core: boolean;
    semesters: string[];
  }>({
    course_id: '',
    program_id: initialProgramId,
    name: '',
    description: '',
    duration_minutes: 55,
    is_core: false,
    semesters: []
  });
  
  const [loading, setLoading] = useState(isEditing);
  const [saving, setSaving] = useState(false);
  const [errors, setErrors] = useState<{[key: string]: string}>({});
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' as 'success' | 'error' });

  useEffect(() => {
    const loadPrograms = async () => {
      try {
        const data = await programService.getAllPrograms();
        setPrograms(data);
      } catch (error) {
        console.error('Error loading programs:', error);
        setSnackbar({
          open: true,
          message: 'Failed to load programs',
          severity: 'error'
        });
      }
    };

    loadPrograms();
    
    if (isEditing) {
      const loadCourse = async () => {
        try {
          const data = await courseService.getCourseById(id!);
          if (data) {
            // Create a new object that exactly matches your state structure
            setCourse({
              course_id: data.course_id || '',
              program_id: data.program_id || '',
              name: data.name || data.course_name || '',
              description: data.description || '',
              duration_minutes: typeof data.duration_minutes === 'number' ? data.duration_minutes : 55,
              is_core: Boolean(data.is_core),
              semesters: Array.isArray(data.semesters) ? data.semesters : []
            });
          } else {
            setSnackbar({
              open: true,
              message: 'Course not found',
              severity: 'error'
            });
            navigate('/admin/courses');
          }
        } catch (error) {
          console.error('Error loading course:', error);
          setSnackbar({
            open: true,
            message: 'Failed to load course',
            severity: 'error'
          });
        } finally {
          setLoading(false);
        }
      };

      loadCourse();
    } else {
      // For new courses, set an empty course_id instead of generating one
      setCourse(prevCourse => ({
        ...prevCourse,
        course_id: '', // Changed from auto-generated to empty
        program_id: initialProgramId
      }));
      setLoading(false);
    }
  }, [id, isEditing, navigate, initialProgramId]);

  const validateForm = () => {
    const newErrors: {[key: string]: string} = {};
    
    if (!course.course_id.trim()) {
      newErrors.course_id = 'Course ID is required';
    }
    
    if (!course.name.trim()) {
      newErrors.name = 'Course name is required';
    }
    
    if (!course.program_id) {
      newErrors.program_id = 'Program is required';
    }
    
    if (course.duration_minutes === '') {
      newErrors.duration_minutes = 'Duration is required';
    } else if (typeof course.duration_minutes === 'number' && course.duration_minutes <= 0) {
      newErrors.duration_minutes = 'Duration must be greater than 0';
    }
    
    if (course.semesters.length === 0) {
      newErrors.semesters = 'At least one semester must be selected';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }
  
    try {
      setSaving(true);
    
      // Create a properly typed object for the API
      const apiCourse: Partial<Course> = {
        course_id: course.course_id,
        program_id: course.program_id,
        name: course.name,
        description: course.description,
        duration_minutes: Number(course.duration_minutes), // Force conversion to number
        is_core: course.is_core,
        semesters: course.semesters
      };
    
      if (isEditing) {
        await courseService.updateCourse(id!, apiCourse);
      } else {
        await courseService.createCourse(apiCourse);
      }
    
      setSnackbar({
        open: true,
        message: `Course ${isEditing ? 'updated' : 'created'} successfully`,
        severity: 'success'
      });
    
      // Navigate back after a brief delay
      setTimeout(() => {
        navigate('/admin/courses');
      }, 1500);
    } catch (error) {
      console.error('Error saving course:', error);
      setSnackbar({
        open: true,
        message: `Failed to ${isEditing ? 'update' : 'create'} course`,
        severity: 'error'
      });
    } finally {
      setSaving(false);
    }
  };

  const handleChange = (field: string) => (e: React.ChangeEvent<HTMLInputElement>) => {
    setCourse({
      ...course,
      [field]: e.target.value
    });
    
    // Clear the error for this field if it exists
    if (errors[field]) {
      setErrors({
        ...errors,
        [field]: ''
      });
    }
  };

  const handleProgramChange = (event: SelectChangeEvent) => {
    const newProgramId = event.target.value;
    setCourse({
      ...course,
      program_id: newProgramId
    });
    
    // Clear any program_id error
    if (errors.program_id) {
      setErrors({
        ...errors,
        program_id: ''
      });
    }
  };

  const handleDurationChange = (value: number | '') => {
    setCourse({
      ...course,
      duration_minutes: value
    });
    
    // Clear any duration error
    if (errors.duration_minutes) {
      setErrors({
        ...errors,
        duration_minutes: ''
      });
    }
  };

  const handleCoreChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setCourse({
      ...course,
      is_core: event.target.checked
    });
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box>
      <Breadcrumbs sx={{ mb: 2 }}>
        <MuiLink component={Link} to="/admin/courses" underline="hover" color="inherit">
          Courses
        </MuiLink>
        <Typography color="text.primary">
          {isEditing ? 'Edit Course' : 'New Course'}
        </Typography>
      </Breadcrumbs>

      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4" component="h1">
          {isEditing ? 'Edit Course' : 'New Course'}
        </Typography>
        <Button
          variant="outlined"
          color="primary"
          startIcon={<ArrowBackIcon />}
          component={Link}
          to="/admin/courses"
        >
          Back to List
        </Button>
      </Box>

      <Paper sx={{ p: 3 }}>
        <Box component="form" onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            <Grid item xs={12} md={6}>
              <TextInput
                id="course_id"
                label="Course ID"
                value={course.course_id}
                onChange={handleChange('course_id')}
                error={errors.course_id}
                required
                disabled={isEditing} // ID can't be changed once created
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <FormControl fullWidth margin="normal" error={!!errors.program_id}>
                <InputLabel id="program-select-label">Program *</InputLabel>
                <Select
                  labelId="program-select-label"
                  id="program_id"
                  value={course.program_id}
                  label="Program *"
                  onChange={handleProgramChange}
                  disabled={isEditing} // Can't change program once created
                  required
                >
                  <MenuItem value="">
                    <em>Please select a program</em>
                  </MenuItem>
                  {programs.map((prog) => (
                    <MenuItem key={prog.program_id} value={prog.program_id}>
                      {prog.name}
                    </MenuItem>
                  ))}
                </Select>
                {errors.program_id && (
                  <Typography color="error" variant="caption">
                    {errors.program_id}
                  </Typography>
                )}
              </FormControl>
            </Grid>
            <Grid item xs={12}>
              <TextInput
                id="name"
                label="Course Name"
                value={course.name}
                onChange={handleChange('name')}
                error={errors.name}
                required
              />
            </Grid>
            
            <Grid item xs={12} md={6}>
              <NumberInput
                id="duration_minutes"
                label="Duration (minutes)"
                value={course.duration_minutes}
                onChange={handleDurationChange}
                error={errors.duration_minutes}
                required
                min={1}
                max={300}
              />
            </Grid>
            <Grid item xs={12} md={6} sx={{ display: 'flex', alignItems: 'center' }}>
              <FormControlLabel
                control={
                  <Switch
                    checked={course.is_core}
                    onChange={handleCoreChange}
                    color="primary"
                  />
                }
                label="Core Course"
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <Typography variant="subtitle2" gutterBottom>
                Semester
              </Typography>
              <Box sx={{ display: 'flex', gap: 2 }}>
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={course.semesters.includes('Fall')}
                      onChange={(e) => {
                        const { checked } = e.target;
                        setCourse(prev => ({
                          ...prev,
                          semesters: checked 
                            ? [...prev.semesters, 'Fall'] 
                            : prev.semesters.filter(sem => sem !== 'Fall')
                        }));
                      }}
                    />
                  }
                  label="Fall"
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={course.semesters.includes('Spring')}
                      onChange={(e) => {
                        const { checked } = e.target;
                        setCourse(prev => ({
                          ...prev,
                          semesters: checked 
                            ? [...prev.semesters, 'Spring'] 
                            : prev.semesters.filter(sem => sem !== 'Spring')
                        }));
                      }}
                    />
                  }
                  label="Spring"
                />
              </Box>
              {errors.semesters && (
                <Typography color="error" variant="caption">
                  {errors.semesters}
                </Typography>
              )}
            </Grid>
            <Grid item xs={12} sx={{ mt: 2, display: 'flex', justifyContent: 'flex-end' }}>
              <Button
                type="submit"
                variant="contained"
                color="primary"
                startIcon={<SaveIcon />}
                disabled={saving}
                sx={{ bgcolor: '#00539F' }}
              >
                {saving ? (
                  <>
                    <CircularProgress size={24} sx={{ mr: 1, color: 'white' }} />
                    Saving...
                  </>
                ) : (
                  'Save Course'
                )}
              </Button>
            </Grid>
          </Grid>
        </Box>
      </Paper>

      <Snackbar
        open={snackbar.open}
        autoHideDuration={6000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert onClose={() => setSnackbar({ ...snackbar, open: false })} severity={snackbar.severity}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default CourseForm;
