// src/services/courseService.ts

// Updated Course interface with correct fields
export interface Course {
  course_id: string;
  program_id: string;
  department_id: string; // Added as required field
  name: string; 
  course_name?: string; // For backend compatibility
  description: string;
  duration_minutes: number;
  is_core: boolean;
  semesters: string[];
  created_at?: string;
  updated_at?: string;
  program?: any;
  prerequisites?: any[];
}

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3000/api';

// Get courses by program
export const getCoursesByProgram = async (programId: string): Promise<Course[]> => {
  try {
    const token = localStorage.getItem('token');
    const response = await fetch(`${API_URL}/courses/program/${programId}`, {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    });

    if (!response.ok) {
      throw new Error('Failed to fetch courses by program');
    }

    return await response.json();
  } catch (error) {
    console.error(`Error fetching courses for program ${programId}:`, error);
    return [];
  }
};

// Create a new course
export const createCourse = async (course: Partial<Course>): Promise<Course> => {
  try {
    const token = localStorage.getItem('token');
    
    // Map the frontend course model to match what the backend expects
    const backendCourse = {
      course_id: course.course_id,
      department_id: course.department_id,
      course_name: course.name, // The backend expects course_name, not name
      duration_minutes: course.duration_minutes,
      is_core: course.is_core,
      program_ids: [course.program_id] // Backend expects an array of program IDs
    };
    
    console.log('Sending to backend:', backendCourse); // Debug log
    
    const response = await fetch(`${API_URL}/courses`, {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(backendCourse)
    });

    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.message || 'Failed to create course');
    }

    return await response.json();
  } catch (error) {
    console.error('Error creating course:', error);
    throw error;
  }
};

// Update a course
export const updateCourse = async (id: string, course: Partial<Course>): Promise<Course> => {
  try {
    const token = localStorage.getItem('token');
    
    // Map the frontend course model to match what the backend expects
    const backendCourse = {
      course_name: course.name, // The backend expects course_name, not name
      department_id: course.department_id,
      duration_minutes: course.duration_minutes,
      is_core: course.is_core,
      program_ids: course.program_id ? [course.program_id] : undefined // Backend expects an array of program IDs
    };
    
    const response = await fetch(`${API_URL}/courses/${id}`, {
      method: 'PUT',
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(backendCourse)
    });

    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.message || 'Failed to update course');
    }

    return await response.json();
  } catch (error) {
    console.error(`Error updating course ${id}:`, error);
    throw error;
  }
};

// Delete a course
export const deleteCourse = async (id: string): Promise<{ success: boolean; message: string }> => {
  try {
    const token = localStorage.getItem('token');
    const response = await fetch(`${API_URL}/courses/${id}`, {
      method: 'DELETE',
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    });

    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.message || 'Failed to delete course');
    }

    // Handle 204 No Content or empty responses
    if (response.status === 204 || response.headers.get('content-length') === '0') {
      return {
        success: true,
        message: 'Course deleted successfully'
      };
    }

    const data = await response.json();
    return {
      success: true,
      message: data.message || 'Course deleted successfully'
    };
  } catch (error) {
    console.error(`Error deleting course ${id}:`, error);
    throw error;
  }
};

// Delete multiple courses
export const deleteCourses = async (ids: string[]): Promise<{ success: boolean; message: string }> => {
  try {
    const token = localStorage.getItem('token');
    const response = await fetch(`${API_URL}/courses/batch-delete`, {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ ids })
    });

    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.message || 'Failed to delete courses');
    }

    const data = await response.json();
    return {
      success: true,
      message: data.message || 'Courses deleted successfully'
    };
  } catch (error) {
    console.error(`Error deleting courses:`, error);
    throw error;
  }
};

const courseService = {
  getAllCourses: async (): Promise<Course[]> => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`${API_URL}/courses`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error('Failed to fetch courses');
      }

      return await response.json();
    } catch (error) {
      console.error('Error fetching courses:', error);
      return [];
    }
  },

  getCourseById: async (id: string): Promise<Course | null> => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`${API_URL}/courses/${id}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error('Failed to fetch course');
      }

      return await response.json();
    } catch (error) {
      console.error(`Error fetching course ${id}:`, error);
      return null;
    }
  },

  getCoursesByProfessor: async (professorId: string): Promise<Course[]> => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`${API_URL}/courses/professor/${professorId}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error('Failed to fetch courses by professor');
      }

      return await response.json();
    } catch (error) {
      console.error(`Error fetching courses for professor ${professorId}:`, error);
      return [];
    }
  },
  
  // Including the new methods in the courseService object
  getCoursesByProgram,
  createCourse,
  updateCourse,
  deleteCourse,
  deleteCourses
};

export default courseService;
